import React, { useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Fade from 'react-reveal/Fade';
import { FaPlay, FaCode } from 'react-icons/fa';

import { ThemeContext } from '../../contexts/ThemeContext';

import eduImgWhite from '../../assets/svg/education/eduImgWhite.svg'
import eduImgBlack from '../../assets/svg/education/eduImgBlack.svg'
import './Education.css'

function EducationCard({ id, institution, course, startYear, endYear }) {

    const { theme } = useContext(ThemeContext);

    const useStyles = makeStyles(() => ({
        educationCard: {
            backgroundColor: theme.primary30,
            "&:hover": {
                backgroundColor: theme.primary50,
            },
            perspective: '1000px', // Updated perspective for flip effect
        },
        flipCardInner: {
            position: 'relative',
            width: '100%', // Adjusted to fill the card
            height: '100%', // Adjusted to fill the card
            textAlign: 'center',
            transition: 'transform 0.8s',
            transformStyle: 'preserve-3d',
        },
        flipCardFrontBack: {
            position: 'absolute',
            width: '100%',
            height: '100%',
            backfaceVisibility: 'hidden',
        },
        flipCardBack: {
            transform: 'rotateY(180deg)',
        }
    }));

    const classes = useStyles();

    return (
        <Fade bottom>
            <div key={id} className={`education-card ${classes.educationCard}`} >
                <div className="educard-img" style={{ backgroundColor: theme.primary }}>
                    <img src={theme.type === 'light' ? eduImgBlack : eduImgWhite} alt="" />
                </div>
                <p
                    className='education--desc'
                    style={{
                        background: theme.secondary,
                        color: theme.tertiary,
                    }}
                >
                    hi
                </p>
                <div className="education-details">
                    <h6 style={{ color: theme.primary }}>{startYear}-{endYear}</h6>
                    <h4 style={{ color: theme.tertiary }}>{institution}</h4>
                    <h5 style={{ color: theme.tertiary80 }}>{course}</h5>
                </div>
            </div>
            <div class="education-card-flip">
                <div class="flip-card-inner">
                    <div class="flip-card-front">
                        <h1>Front of Card</h1>
                    </div>
                    <div class="flip-card-back">
                        <h1>Back of Card</h1>
                    </div>
                </div>
            </div>
        </Fade>
    )
}

export default EducationCard
