import React, { useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Fade from 'react-reveal/Fade';
import { FaPlay, FaCode } from 'react-icons/fa';

import { ThemeContext } from '../../contexts/ThemeContext';

import eduImgWhite from '../../assets/svg/education/eduImgWhite.svg'
import eduImgBlack from '../../assets/svg/education/eduImgBlack.svg'
import './Education.css'

function EducationCard({ id, institution, course, startYear, endYear }) {

    const { theme } = useContext(ThemeContext);

    const useStyles = makeStyles(() => ({
        educationCard: {
            backgroundColor: theme.primary30,
            "&:hover": {
                backgroundColor: theme.primary50,
            },
            perspective: '1000px', // Updated perspective for flip effect
        },
        flipCardInner: {
            position: 'relative',
            width: '100%', // Adjusted to fill the card
            height: '100%', // Adjusted to fill the card
            textAlign: 'center',
            transition: 'transform 0.8s',
            transformStyle: 'preserve-3d',
        },
        flipCardFrontBack: {
            position: 'absolute',
            width: '100%',
            height: '100%',
            backfaceVisibility: 'hidden',
        },
        flipCardBack: {
            transform: 'rotateY(180deg)',
        }
    }));

    const classes = useStyles();

    return (
        <Fade bottom>
            <div key={id} className={`education-card ${classes.educationCard}`}>
                {/* Existing content of the education card */}
                <div className="education-details">
                    {/* ... */}
                </div>
            </div>
            <div className={`education-card-flip ${classes.educationCard}`}>
                <div className={classes.flipCardInner}>
                    <div className={`flip-card-front ${classes.flipCardFrontBack}`}>
                        {/* Front of the flip card */}
                        <h1>Front of Card</h1>
                    </div>
                    <div className={`flip-card-back ${classes.flipCardFrontBack} ${classes.flipCardBack}`}>
                        {/* Back of the flip card */}
                        <h1>Back of Card</h1>
                    </div>
                </div>
            </div>
        </Fade>
    )
}

export default EducationCard
