import React, { useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Fade from 'react-reveal/Fade';

import { ThemeContext } from '../../contexts/ThemeContext';
import eduImgWhite from '../../assets/svg/education/eduImgWhite.svg';
import eduImgBlack from '../../assets/svg/education/eduImgBlack.svg';

function EducationCard({ id, institution, course, startYear, endYear }) {
    const [isFlipped, setIsFlipped] = useState(false);
    const { theme } = useContext(ThemeContext);

    const useStyles = makeStyles(() => ({
        flipCardFront: {
            // front side styles
        },
        flipCardBack: {
            // back side styles
        },
        educationCard: {
            // Add your existing styles for educationCard here
        },
    }));

    const classes = useStyles();

    const handleFlip = () => {
        setIsFlipped(!isFlipped);
    };

    return (
        <Fade bottom>
            <div key={id} className={`flip-card ${isFlipped ? 'flipped' : ''}`} onClick={handleFlip}>
                <div className="flip-card-inner">
                    <div className={`flip-card-front ${classes.educationCard}`}>
                        {/* Front side content */}
                        <div className="educard-img" style={{ backgroundColor: theme.primary }}>
                            <img src={theme.type === 'light' ? eduImgBlack : eduImgWhite} alt="" />
                        </div>
                        {/* ... other front side elements ... */}
                    </div>
                    <div className="flip-card-back">
                        {/* Back side content */}
                        <h6 style={{ color: theme.primary }}>{startYear}-{endYear}</h6>
                        <h4 style={{ color: theme.tertiary }}>{institution}</h4>
                        <h5 style={{ color: theme.tertiary80 }}>{course}</h5>
                        {/* ... other back side elements ... */}
                    </div>
                </div>
            </div>
        </Fade>
    );
}

export default EducationCard;
